import { ChangeDetectionStrategy, ChangeDetectorRef, Component, OnDestroy, OnInit, ViewEncapsulation } from '@angular/core'; 
import { FormBuilder, FormGroup } from '@angular/forms';
import { StorageService } from '../../../auth/api-authenticate/storage.service';
 

@Component({
    selector       : 'project',
    templateUrl    : './project.component.html',
    encapsulation  : ViewEncapsulation.None,
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProjectComponent implements OnInit, OnDestroy
{
    formGroup: FormGroup; 
    userlogged: any;
    /**
     * Constructor
     */
    constructor(
        private cdr: ChangeDetectorRef,
        private formBuilder: FormBuilder,
        private _storageService: StorageService
    )
    {
        // temp user
        let _send = {
            Rota: 'user/connected', 
            email: 'iso-parametrizador@giso.com',
            password: 'admin'
          } 
        this._storageService.resetUser(_send);

        const currentUserString = localStorage.getItem('currentUser'); 
        if (currentUserString !== null) {
            this.userlogged = JSON.parse(currentUserString); 
        }  
    }
 
    ngOnInit(): void
    { 

    }
 
    ngOnDestroy(): void
    {
 
    }
 
}
<div class="flex flex-col flex-auto min-w-0">

    <!-- Header -->
    <div class="bg-card">
        <div class="flex flex-col w-full max-w-screen-xl mx-auto px-6 sm:px-8">
            <div class="flex flex-col sm:flex-row flex-auto sm:items-center min-w-0 my-8 sm:my-12">
                <!-- Avatar and name -->
                <div class="flex flex-auto items-center min-w-0">
                    <div class="flex-0 w-16 h-16 rounded-full overflow-hidden">
                        <img
                            class="w-full h-full object-cover"
                            src="assets/images/avatars/logopago.jpeg">
                    </div>
                    <div class="flex flex-col min-w-0 ml-4">
                        <ng-container *transloco="let t">
                            <div class="text-2xl md:text-5xl font-semibold tracking-tight leading-7 md:leading-snug truncate">{{t('welcome-back')}}, {{userlogged.name}}!</div>
                        </ng-container>
                        <div class="flex items-center">
                            <mat-icon
                                class="icon-size-5"
                                [svgIcon]="'heroicons_solid:bell'"></mat-icon>
                            <div class="ml-1.5 leading-6 truncate text-secondary">You have 2 new messages...</div>
                        </div>
                    </div>
                </div>
                <!-- Actions -->
                <div class="flex items-center mt-6 sm:mt-0 sm:ml-2 space-x-3">
                    <button
                        class="bg-accent"
                        mat-flat-button
                        [color]="'accent'">
                        <mat-icon
                            class="icon-size-5"
                            [svgIcon]="'heroicons_solid:cog'"></mat-icon>
                        <span class="ml-2">Formula</span>
                    </button>
                    <button
                        mat-flat-button
                        [color]="'primary'">
                        <mat-icon
                            class="icon-size-5"
                            [svgIcon]="'heroicons_solid:cog'"></mat-icon>
                        <span class="ml-2">Macros</span>
                    </button>
                </div>
            </div>
 
        </div>
    </div>
 
    
    <div> 
   
    </div>
 
</div>
